mapper.update:
update sys_user
username = #{model.username}, password = #{model.password}, realname = #{model.realname}, phone = #{model.phone}, email = #{model.email}, weichat = #{model.weichat}, qqcode = #{model.qqcode}, sex = #{model.sex}, birthdate = #{model.birthdate}, deptid = #{model.deptid}, onduty = #{model.onduty}, belarea = #{model.belarea}, updateby = #{model.updateby}, updatetime = #{model.updatetime},
where userid = #{model.userid}
